mapper.select:
select ui_num "uiNum", ui_name "uiName", ui_id "uiId",
credat, cretim, moddat, modtim, active
from user_info

	
		and ui_name = #{uiName}
	
	
		and ui_num = #{uiNum}

select ui_num "uiNum", ui_name "uiName", ui_id "uiId",
credat, cretim, moddat, modtim, active
from user_info

	
		and ui_id = #{uiId}
	
	
		and ui_num = #{uiNum}

mapper.delete:
update user_info
set ui_name = #{uiName},
moddat = to_char(sysdate,'YYYYMMDD'),
modtim = to_char(sysdate,'HH24MISS'),
active = ${active}
where ui_num=#{uiNum}
